$victim = Read-Host -Prompt 'Specify server name to kill'; $victimfqdn = (Get-ADComputer $victim).DNSHostName; $victimip = (resolve-dnsname $victimfqdn  -server 127.0.0.1).IPAddress; $victimdotsite = $victimfqdn -replace "."+(get-addomain).dnsroot -replace ""; Get-ADComputer -Identity $victim | Remove-ADObject -Recursive -Confirm:$false; Remove-DnsServerResourceRecord -RRType A -zonename (get-addomain).dnsroot -Name $victimdotsite -RecordData $victimip -PassThru -Force
